package br.com.acpirassununga.controledehoras;import org.springframework.web.bind.annotation.GetMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import java.time.Year;import java.util.Arrays;import java.util.List;@RestController@RequestMapping("/airplanes")public class AirplanesController {    public static final List<Airplane> AIRPLANE_LIST = Arrays.asList(            Airplane.builder().id("PP-GCL").yearOfFactory(Year.of(1976)).model("AB-180").brand("Aeroboero").build(),            Airplane.builder().id("PP-HLX").yearOfFactory(Year.of(1976)).model("P-56C").brand("Neiva").build()    );    @GetMapping("all")    public List<Airplane> getAllAirplanes() {        return AIRPLANE_LIST;    }}