package br.com.acpirassununga.controledehoras;import org.springframework.boot.SpringApplication;import org.springframework.boot.autoconfigure.EnableAutoConfiguration;import org.springframework.boot.autoconfigure.SpringBootApplication;import org.springframework.context.annotation.ComponentScan;import org.springframework.context.annotation.ComponentScans;import org.springframework.context.annotation.EnableAspectJAutoProxy;@SpringBootApplication@EnableAutoConfiguration@EnableAspectJAutoProxy@ComponentScans(	{		@ComponentScan(basePackageClasses = {			AirplanesController.class,		})	})public class ControleDeHorasApplication {	public static void main(String[] args) {		SpringApplication.run(ControleDeHorasApplication.class, args);	}}